CREATE TABLE `back-compre-local`.`comercio` (`ID` INT NOT NULL , `IMAGEM` VARCHAR(200) NOT NULL , `ENDERECO` VARCHAR(200) NOT NULL , `TELEFONE` VARCHAR(200) NOT NULL , `HORARIO_ENTRADA` VARCHAR(200) NOT NULL , `HORARIO_SAIDA` VARCHAR(200) NOT NULL , `HORARIO_ENTRADA_TARDE` VARCHAR(200) NOT NULL , `HORARIO_SAIDA_TARDE` VARCHAR(200) NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;
CREATE TABLE `back-compre-local`.`frete` (`ID` INT NOT NULL , `ID_COMERCIO` INT NOT NULL , `DISTANCIA_MAXIMA` VARCHAR(200) NOT NULL , `TIPO` VARCHAR(200) NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;
CREATE TABLE `back-compre-local`.`valor_frete` (`ID` INT NOT NULL , `ID_FRETE` INT NOT NULL , `VALOR` VARCHAR(200) NOT NULL , `DISTANCIA_LIMITE` VARCHAR(200) NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;
CREATE TABLE `back-compre-local`.`produto` (`ID` INT NOT NULL , `ID_COMERCIO` INT NOT NULL , `NOME` VARCHAR NOT NULL , `IMAGEM` VARCHAR(200) NOT NULL , `PRECO` VARCHAR(200) NOT NULL , `ESTOQUE` VARCHAR(200) NOT NULL , `TIPO_VENDA` VARCHAR(20) NOT NULL , PRIMARY KEY (`ID`));
CREATE TABLE `back-compre-local`.`venda` (`ID` INT NOT NULL , `ID_COMERCIO` INT NOT NULL , `ID_PRODUTO` INT NOT NULL , `TIPO` VARCHAR(200) NOT NULL , `VALOR` VARCHAR(200) NOT NULL , `DATA_PEDIDO` VARCHAR(200) NOT NULL , `DATA_ENTREGA` VARCHAR(200) NOT NULL , `VOLUME` VARCHAR(200) NOT NULL , `QUANTIDADE` VARCHAR(200) NOT NULL , `PAGAMENTO` VARCHAR(200) NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;


